product.prod_name LIKE '%'||#{searchKeyword}||'%'
product.price LIKE '%'||#{searchKeyword}||'%'
ORDER BY product.prod_no ) inner_table WHERE ROWNUM < = #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

