mapper.update:
UPDATE product
PROD_NAME = #{prodName} , PROD_DETAIL = #{prodDetail}, MANUFACTURE_DAY = #{manuDate} , PRICE = #{price} , IMAGE_FILE = #{fileName}
WHERE PROD_NO = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM (SELECT product.* , transaction.tran_status_code FROM product JOIN transaction ON product.prod_no = transaction.prod_no(+)
product.prod_no = #{searchKeyword}
product.prod_name LIKE '%'||#{searchKeyword}||'%'
product.price LIKE '%'||#{searchKeyword}||'%'
ORDER BY product.prod_no ) inner_table WHERE ROWNUM < = #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
